mapper.select:
select * from (select a.*, rownum rnum from (select b.board_num,replace(replace(b.title,'<','&lt;'),'>','&gt;') title,b.hit,b.reg_date,m.id from spboard b join spmember m on b.mem_num = m.mem_num
b.title like '%' || #{keyword} || '%' m.id like '%' || #{keyword} || '%' b.content like '%' || #{keyword} || '%' b.title like '%' || #{keyword} || '%' or b.content like '%' || #{keyword} || '%'
order by b.board_num desc)a) where rnum >= #{start} and rnum <= #{end}

mapper.update:
update spboard set
uploadfile=#{uploadfile}, filename=#{filename},
title=#{title},content=#{content},ip=#{ip},modify_date=sysdate where board_num=#{board_num}

mapper.select:
select *
from (select a.*,rownum rnum
	  from (select re_num,replace(replace(re_content,'<','&lt;'),'>','&gt;') re_content,re_date,re_mdate,board_num,mem_num,id
	  		from spboard_reply join spmember using(mem_num)
	  		where board_num = #{board_num}
	  		order by re_num desc)a)

where rnum >= #{start} and rnum <= #{end}
